create database camera;
use camera;
CREATE TABLE devicedirectory (id INT NOT NULL COMMENT '序号ID' AUTO_INCREMENT, gatewayid VARCHAR (100) DEFAULT NULL COMMENT '网关ID', parentid VARCHAR (100) DEFAULT NULL COMMENT '父设备/区域/系统ID（可必选)', deviceid VARCHAR (100) DEFAULT NULL COMMENT '设备ID--设备/区域/系统编码（必选）', devicename VARCHAR (100) DEFAULT NULL COMMENT '设备名称--设备/区域/系统名称', firmsname VARCHAR (100) DEFAULT NULL COMMENT '厂商名称', devicetype VARCHAR (100) DEFAULT NULL COMMENT '设备型号', ownerto VARCHAR (100) DEFAULT NULL COMMENT '设备归属', civilcode VARCHAR (100) DEFAULT NULL COMMENT '行政区域', address VARCHAR (100) DEFAULT NULL COMMENT '安装地址', registertype VARCHAR (100) DEFAULT NULL COMMENT '注册方式 缺省为1   1： 符合sip3261标准的认证注册模式；2： 基于口令的双向认证注册模式；3： 基于数字证书的双向认证注册模式 ', secrecy VARCHAR (100) DEFAULT NULL COMMENT '保密属性', username VARCHAR (100) DEFAULT NULL COMMENT '用户名', deviceip VARCHAR (100) DEFAULT NULL COMMENT '设备ip--设备/区域/系统IP地址（可选）', deviceport VARCHAR (100) DEFAULT NULL COMMENT '设备端口--设备/区域/系统端口（可选）', userpwd VARCHAR (100) DEFAULT NULL COMMENT '用户密码', longitude VARCHAR (100) DEFAULT NULL COMMENT '经度', latitude VARCHAR (100) DEFAULT NULL COMMENT '纬度', mediaformat VARCHAR (100) DEFAULT NULL COMMENT '媒体格式', devstorage INT DEFAULT NULL COMMENT '接入库名称ID', nodetype INT DEFAULT NULL COMMENT '节点类型', block VARCHAR (100) DEFAULT NULL COMMENT '警区（可选）', parental VARCHAR (100) DEFAULT NULL COMMENT '当为设备时，是否有子设备（必选）1有，0没有', safetyway VARCHAR (100) DEFAULT NULL COMMENT '信令安全模式（可选）缺省为0； 0：不采用；2：S/MIME签名方式；3：S/MIME加密签名同时采用方式；4：数字摘要方式', certnum VARCHAR (100) DEFAULT NULL COMMENT '证书序列号', certifiable VARCHAR (100) DEFAULT NULL COMMENT '证书有效标识（有证书的设备必选）缺省为0；证书有效标识： 0：无效 1：有效', errcode VARCHAR (100) DEFAULT NULL COMMENT '无效原因码', endtime VARCHAR (100) DEFAULT NULL COMMENT '证书终止有效期', devstatus VARCHAR (100) DEFAULT NULL COMMENT '设备状态', ptz VARCHAR (100) DEFAULT NULL COMMENT '摄像机类型扩展，标识摄像机类型：1-球机；2-半球；3-固定枪机；4-遥控枪机。当目录项为摄像机时可选。', position VARCHAR (100) DEFAULT NULL COMMENT '摄像机位置类型扩展。1-省际检查站、2-党政机关、3-车站码头、4-中心广场、5-体育场馆、6-商业中心、7-宗教场所、8-校园周边、9-治安复杂区域、10-交通干线。当目录项为摄像机时可选', room VARCHAR (100) DEFAULT NULL COMMENT '摄像机安装位置室外、室内属性。1-室外、2-室内。当目录项为摄像机时可选，缺省为1', application VARCHAR (100) DEFAULT NULL COMMENT '摄像机用途属性。1-治安、2-交通、3-重点。当目录项为摄像机时可选', supplylight VARCHAR (100) DEFAULT NULL COMMENT '摄像机补光属性。1-无补光、2-红外补光、3-白光补光。当目录项为摄像机时可选，缺省为1', direction VARCHAR (100) DEFAULT NULL COMMENT '摄像机监视方位属性。1-东、2-西、3-南、4-北、5-东南、6-东北、7-西南、8-西北。当目录项为摄像机时且为固定摄像机或设置看守位摄像机时可选', resolution VARCHAR (100) DEFAULT NULL COMMENT '摄像机支持的分辨率，可有多个分辨率值，各个取值见以“/”分隔。分辨率取值参见国标附录F中SDP f字段规定。当目录项为摄像机时可选', businessgroup VARCHAR (100) DEFAULT NULL COMMENT '虚拟组织所属的业务分组ID，业务分组根据特定的业务需求制定，一个业务分组包含一组特定的虚拟组织', channel VARCHAR (100) DEFAULT NULL COMMENT 'PVG对应的AV通道(PVG通道专用)', islogin INT DEFAULT NULL COMMENT '是否需要登录', lowplatdevsn INT DEFAULT NULL COMMENT '接入下级平台的平台、区域及设备的id', share INT DEFAULT NULL COMMENT '该设备是否共享', online INT DEFAULT NULL COMMENT '设备在线状态： 0 在线 ， 1 不在线', state INT DEFAULT NULL COMMENT '当前列的状态', PRIMARY KEY (id)) ENGINE=InnoDB CHARSET=utf8;
